(274, '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n/**\r\n * Created by PhpStorm.\r\n * User: zhibo\r\n * 15-6-15 下午9:02\r\n * Mail:ni5400@163.com\r\n * Web:http://www.ki35.com\r\n */\r\n\r\nnamespace Admin\\Controller;\r\nuse Think\\Controller;\r\n\r\nclass UserController extends CommonController {\r\n\r\n\r\n    //ajax检测用户是否存在\r\n    public function checkUser(){\r\n        if(!IS_AJAX) $this-&amp;gt;error(''非法操作'');\r\n        if(D(''User'')-&amp;gt;checkUsername(I(''post.username''))){\r\n            echo ''false'';\r\n        }else{\r\n            echo ''true'';\r\n        };\r\n    }\r\n    //添加管理员处理\r\n    public function manager_addHandel(){\r\n        if(!IS_POST) $this-&amp;gt;error(''非法操作'');\r\n        $data=array(\r\n            ''username''=&amp;gt;$username=I(''post.username''),\r\n            ''byname''=&amp;gt;$byname=I(''post.byname''),\r\n            ''password''=&amp;gt;$password=I(''post.password''),\r\n            ''group''=&amp;gt;$group=I(''post.group''),\r\n            ''status''=&amp;gt;$status=I(''status'','''',''intval'')\r\n        );\r\n        if(!$group) $this-&amp;gt;error(''未选择分组'');\r\n        //写入model\r\n        $User=D(''User'');\r\n        $addResult=$User-&amp;gt;register($username,$byname,$password,$status);\r\n        if($msg=$User-&amp;gt;getError()){\r\n            $this-&amp;gt;error($msg);  //自动验证未通过抛出错误\r\n        }\r\n        //写入user表后执行写入AuthGroupAccess\r\n        if($addResult){\r\n            $groupData=array(\r\n                ''uid''=&amp;gt;$addResult,\r\n                ''group_id''=&amp;gt;$group\r\n            );\r\n            //角色id插入表AuthGroupAccess\r\n            if($AuthGroupAccess=M(''AuthGroupAccess'')-&amp;gt;add($groupData)){\r\n                $this-&amp;gt;success(''添加成功'',U(''Setting/manager''));\r\n            }else{\r\n                $this-&amp;gt;error(''写入AuthGroupAccess表失败'');\r\n            }\r\n        }else{\r\n            $this-&amp;gt;error(''写入User表失败'');\r\n        }\r\n\r\n    }\r\n\r\n    //修改管理员处理\r\n    public function manager_editHandel(){\r\n        if(!IS_POST) $this-&amp;gt;error(''非法操作'');\r\n        $data=array(\r\n            ''id''=&amp;gt;$uid=I(''post.id''),\r\n            ''user_byname''=&amp;gt;$byname=I(''post.byname''),\r\n            ''group''=&amp;gt;$group=I(''post.group''),\r\n            ''status''=&amp;gt;$status=I(''status'','''',''intval'')\r\n        );\r\n        if(!$group) $this-&amp;gt;error(''分组未选择'');\r\n        if($_POST[''password'']) $data[''password'']=sha1(I(''post.password''));\r\n        $User = D(&quot;User&quot;); // 实例化User对象\r\n        if (!$User-&amp;gt;create($data)){\r\n            $this-&amp;gt;error($User-&amp;gt;getError());\r\n        }else{\r\n            $groupMap[''uid'']=$uid;\r\n            $groupData[''group_id'']=$group;\r\n            if(M(''AuthGroupAccess'')-&amp;gt;where($groupMap)-&amp;gt;save($groupData) || $User-&amp;gt;save($data) ){\r\n                $this-&amp;gt;success(''修改成功'',U(''Setting/manager''));\r\n            }else{\r\n                $this-&amp;gt;error(''修改失败或数据未更改'');\r\n            }\r\n        }\r\n\r\n    }\r\n\r\n\r\n    /**\r\n     * 添加管理员处理表单\r\n     * 由于用户对应多角色存在bug暂时弃用\r\n     */\r\n    public function manager_addHandel2(){\r\n        //先判断是否选择分组（分组是数组集）若分组未选择抛出错误提示\r\n        $groupPost=$_POST[''group''];\r\n        foreach($groupPost as $key=&amp;gt;$value ){\r\n            if($value=='''')  $this-&amp;gt;error(''角色未选择'');\r\n        }\r\n        $data=array(\r\n            ''username''=&amp;gt;$username=I(''post.username''),\r\n            ''byname''=&amp;gt;$byname=I(''post.byname''),\r\n            ''password''=&amp;gt;$password=I(''post.password''),\r\n            ''status''=&amp;gt;$status=I(''status'','''',''intval'')\r\n        );\r\n        P($_POST);\r\n        P($data);\r\n        die;\r\n        //写入model\r\n        $User=D(''User'');\r\n        $addResult=$User-&amp;gt;register($username,$byname,$password,$status);\r\n        //自动验证未通过抛出错误\r\n        if($msg=$User-&amp;gt;getError()){\r\n            $this-&amp;gt;error($msg);\r\n        }\r\n        //写入user表后执行写入AuthGroupAccess\r\n        if($addResult){\r\n            $groupPost=array_unique($groupPost);\r\n            $group=array();\r\n            foreach($groupPost as $key=&amp;gt;$value ){\r\n                $group[$key][''uid'']=$addResult;\r\n                $group[$key][''group_id'']=$value;\r\n            }\r\n            //角色id插入表AuthGroupAccess\r\n            if($AuthGroupAccess=M(''AuthGroupAccess'')-&amp;gt;addAll($group)){\r\n               $this-&amp;gt;success(''添加成功'',U(''Setting/role''));\r\n            }else{\r\n                $this-&amp;gt;error(''写入AuthGroupAccess表失败'');\r\n            }\r\n        }else{\r\n            $this-&amp;gt;error(''写入User表失败'');\r\n        }\r\n\r\n    }\r\n\r\n\r\n}&lt;/pre&gt;', 272),
(278, '&lt;pre class=&quot;brush:php&quot;&gt; \r\nreturn array(\r\n    //设置模版替换变量 注意 __ROOT__.''/''.APP_PATH 里的 ''/''不能省掉\r\n    ''TMPL_PARSE_STRING''=&amp;gt;array(\r\n        ''__CSS__''=&amp;gt;__ROOT__.''/Public/Blog/css'',\r\n        ''__JS__''=&amp;gt;__ROOT__.''/Public/Blog/js'',\r\n        ''__IMG__''=&amp;gt;__ROOT__.''/Public/Blog/images'',\r\n    ),\r\n	//''配置项''=&amp;gt;''配置值''\r\n    ''VIEW_PATH''=&amp;gt;''./Template/Blog/'' ,// 设置默认的视图层名称\r\n    //标签库引入Cx,html是默认的标签库\r\n    ''TAGLIB_PRE_LOAD''=&amp;gt;''Ki35'',\r\n\r\n    //启用路由功能\r\n    ''URL_ROUTER_ON''=&amp;gt;true,\r\n    //配置路由规则\r\n    ''URL_ROUTE_RULES''=&amp;gt;array(\r\n        //每条键值对，对应一个路由规则\r\n        ''i/:domain''=&amp;gt;''Space/index'',\r\n        ''category/:cate_id\\d''=&amp;gt;''Category/index'', //''category/:cate_id''=&amp;gt;''Category/index'',  \\d代表只能匹配数字\r\n        ''/^category_(\\d+)$/''=&amp;gt;''Category/index?cate_id=:1'',//类似category_22\r\n        ''notes/:id\\d''=&amp;gt;''Show/index'', //博客内容页地址\r\n        ''tag/:name''=&amp;gt;''tag/index'',  //tag标签地址1，地址类似tag/apache\r\n        ''/^tag_(\\w+)$/''=&amp;gt;''tag/index?name=:1'',//类似tag_apache\r\n        ''/search.html''=&amp;gt;''Search/search/'',//类似tag_apache\r\n    ),\r\n    //开启静态缓存\r\n    ''HTML_CACHE_ON''=&amp;gt;true,\r\n    ''HTML_CACHE_RULES''=&amp;gt;array(\r\n//        ''Show:index''=&amp;gt;array(''{:module}/notes/{id}'',0),\r\n//        ''Show:error''=&amp;gt;array(''{:module}/notes/'',0),\r\n    ),\r\n    //动态缓存方式\r\n    ''DATA_CACHE_TYPE''=&amp;gt;''file'', //tp默认是file缓存  Memcache\r\n);&lt;/pre&gt;', 280),
(280, '&lt;pre class=&quot;brush:php&quot;&gt; public function search(){\r\n        $keyword=I(''get.keyword'');\r\n        $where[''title'']  = array(''like'', ''%''.$keyword.''%'');\r\n        $where[''introduce'']  = array(''like'', ''%''.$keyword.''%'');\r\n        $where[''_logic''] = ''or'';\r\n        $map[''_complex''] = $where;\r\n        $map[''status'']=1;\r\n\r\n        $order=''add_time Desc'';\r\n        $User = M(''Article'');\r\n        $count      = $User-&amp;gt;where($map)-&amp;gt;count();// 查询满足要求的总记录数\r\n        $Page       = new Page($count,20);  // 每页显示的记录数(25)\r\n        $show       = $Page-&amp;gt;show();// 分页显示输出\r\n        // 进行分页数据查询 注意limit方法的参数要使用Page类的属性\r\n        $list = $User-&amp;gt;where($map)\r\n            -&amp;gt;field(''id,title,add_time,tags,introduce,status,hits,update_time,user_name,thumb'')\r\n            -&amp;gt;order($order)\r\n            -&amp;gt;limit($Page-&amp;gt;firstRow.'',''.$Page-&amp;gt;listRows)\r\n            -&amp;gt;select();\r\n        foreach($list as $value =&amp;gt;$key){\r\n            $list[$value][''tags'']=json_decode($list[$value][''tags''],true);\r\n            $list[$value][''title''] = str_replace($keyword, ''&amp;lt;span class=&quot;blog-search-title&quot;&amp;gt;''.$keyword.''&amp;lt;/span&amp;gt;'', $list[$value][''title'']);\r\n            $list[$value][''introduce''] = str_replace($keyword, ''&amp;lt;span class=&quot;blog-search-text&quot;&amp;gt;''.$keyword.''&amp;lt;/span&amp;gt;'', $list[$value][''introduce'']);\r\n        }\r\n\r\n        if(!$list){\r\n            $this-&amp;gt;assign(''error'',''true'');\r\n        }\r\n        $this-&amp;gt;assign(''list'',$list);// 赋值数据集\r\n        $this-&amp;gt;assign(''page'',$show);// 赋值分页输出\r\n        $this-&amp;gt;display();\r\n\r\n    }&lt;/pre&gt;', 283),
(281, '&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	015年建军节前夕的将官集中晋衔的消息陆续由官方披露。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	其中，在军委总部，2012年底调任中央军委办公厅主任的秦生祥已由少将军衔晋升中将军衔。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	据新华社消息，2015年八一建军节前夕，中共中央总书记、国家主席、中央军委主席习近平7月18日下午专门来到第16集团军视察，中央军委副主席范长龙等参加有关活动。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	而在央视7月19日晚间《新闻联播》报道这条习近平视察底16集团军新闻时，秦生祥已佩戴中将军衔出现在视频镜头中。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	这意味着，2012年12月接任中央军委办公厅主任的秦生祥已在今年八一前夕由少将军衔晋升为中将军衔。\r\n&lt;/p&gt;\r\n&lt;div class=&quot;gg200x300&quot; style=&quot;padding:19px;margin:0px 22px 0px 0px;text-align:center;font-size:0px;border:1px solid #E5E5E5;color:#252525;font-family:宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;iframe src=&quot;http://g.163.com/r?site=netease&amp;amp;affiliate=news&amp;amp;cat=article&amp;amp;type=logo300x250&amp;amp;location=12&quot; width=&quot;300&quot; height=&quot;250&quot; frameborder=&quot;no&quot;&gt;\r\n	&lt;/iframe&gt;\r\n&lt;/div&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	身兼十二届全国人大代表的秦生祥（1957.2）是湖北监利人，曾长期在军委总部工作，历任总政治部组织部副部长、部长等职，2007年前后晋升少将军衔， 2012年12月接替升任副总参谋长的王冠中担任中央军委办公厅主任。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	除秦生祥之外，解放军四总部和军委办公厅等单位近日披露军衔晋升的军官还包括王力和王安龙等。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	澎湃新闻（www.thepaper.cn）稍早前已率先披露，在今年“八一”建军节前夕，总装备部副部长王力已晋升中将军衔，而不久前由南京军区调入军委总部工作的王安龙则晋升少将军衔。\r\n&lt;/p&gt;', 284),
(282, '&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	其中，在军委总部，2012年底调任中央军委办公厅主任的秦生祥已由少将军衔晋升中将军衔。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	据新华社消息，2015年八一建军节前夕，中共中央总书记、国家主席、中央军委主席习近平7月18日下午专门来到第16集团军视察，中央军委副主席范长龙等参加有关活动。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	而在央视7月19日晚间《新闻联播》报道这条习近平视察底16集团军新闻时，秦生祥已佩戴中将军衔出现在视频镜头中。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	这意味着，2012年12月接任中央军委办公厅主任的秦生祥已在今年八一前夕由少将军衔晋升为中将军衔。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	其中，在军委总部，2012年底调任中央军委办公厅主任的秦生祥已由少将军衔晋升中将军衔。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	据新华社消息，2015年八一建军节前夕，中共中央总书记、国家主席、中央军委主席习近平7月18日下午专门来到第16集团军视察，中央军委副主席范长龙等参加有关活动。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	而在央视7月19日晚间《新闻联播》报道这条习近平视察底16集团军新闻时，秦生祥已佩戴中将军衔出现在视频镜头中。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	这意味着，2012年12月接任中央军委办公厅主任的秦生祥已在今年八一前夕由少将军衔晋升为中将军衔。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;text-indent:2em;color:#252525;font-family:宋体, sans-serif;text-align:justify;background-color:#FFFFFF;&quot;&gt;\r\n	其中，在军委总部，2012年底调任中央军委办公厅主任的秦生祥已由少将军衔晋升中将军衔。\r\n&lt;/p&